import React from 'react';
import useSectionHeaderResponsiveSize from './useSectionHeaderResponsiveSize';

/* These are the possible values for the current variant. Use this to change the currentVariant dynamically.
Please don't modify */
const variantOptions = {
  ScreenDesktop: 'ScreenDesktop',
  ScreenTablet: 'ScreenTablet',
  ScreenMobile: 'ScreenMobile',
};

const useSectionHeader = (index: number) => {
  const [currentVariant, setCurrentVariant] = React.useState<string>(
    variantOptions['ScreenDesktop']
  );

  const breakpointsVariant = useSectionHeaderResponsiveSize();

  React.useEffect(() => {
    if (breakpointsVariant !== currentVariant) {
      setCurrentVariant(breakpointsVariant);
    }
  }, [breakpointsVariant]);

  const headerContent = [
    {
      title1: 'Surface',
      title2: 'Innovation',
      tagline: 'Unleashing Potential for Your Modern Workplace',
    },
    {
      title1: 'Seamless',
      title2: 'Enhancements',
      tagline: 'Tailored Accessories for Your Surface Journey',
    },
    {
      title1: 'Hub',
      title2: 'Essentials',
      tagline: 'Amplifying Your Surface Hub Experience',
    }
  ];

  const data: any = { currentVariant, headerContent: headerContent[index] };

  const fns: any = { setCurrentVariant };

  return { data, fns };
};

export default useSectionHeader;
