/**********************************************************************
 *
 *   Responsiveness hook generated by Quest
 *
 *   Responsive Logic for the component goes in this hook
 *   If you want to customize the min/max values for the breakpoints, open the component on Quest Editor.
 *
 *   For help and further details refer to: https://www.quest.ai/docs
 *
 *
 **********************************************************************/

import React from 'react';

const useSidebarResponsiveSize = () => {
  const [variant, setVariant] = React.useState<string>('ScreenTablet');

  React.useEffect(() => {
    const handlerMobile = (e) =>
      setVariant((size: string) => (e.matches ? 'Mobile' : size));
    const MobileSize = window.matchMedia('(max-width: 572px)');
    setVariant((size: string) => (MobileSize.matches ? 'Mobile' : size));
    MobileSize.addEventListener('change', handlerMobile);

    const handlerScreenTablet = (e) =>
      setVariant((size: string) => (e.matches ? 'ScreenTablet' : size));
    const ScreenTabletSize = window.matchMedia('(min-width: 572px)');
    setVariant((size: string) =>
      ScreenTabletSize.matches ? 'ScreenTablet' : size
    );
    ScreenTabletSize.addEventListener('change', handlerScreenTablet);
  }, []);

  return variant;
};

export default useSidebarResponsiveSize;
